#include<bits/stdc++.h>using namespace std;int main(){  int t;  cin>>t;  while(t--){    int n;    cin>>n;    vector<int>a(n);    for(int i=0;i<n;i++){      cin>>a[i];    }    int pcount=0;    int ncount=0;    for(int i=0;i<n;i++){      if(a[i]>0){        pcount++;      }      else{        ncount++;      }    }    int ans=0;    while(pcount<ncount){        pcount++;        ncount--;        ans++;        }    if(ncount%2!=0){        ans++;    }      cout<<ans<<endl;          }  return 0;}