#include<bits/stdc++.h>using namespace std;int main(){  int t;  cin>>t;  while(t--){    int n,k,x;    cin>>n>>k>>x;    bool ans=false;    int sum=0;    if(x!=1){      cout<<"Yes"<<endl;      cout<<n<<endl;      for(int i=0;i<n;i++){        cout<<1<<" ";      }cout<<endl;    }    else{      if(k==1 || ((k==2) && (n%2)!=0)){        cout<<"No"<<endl;      }      else{        cout<<"Yes"<<endl;        if(n%2==0){          cout<<n/2<<endl;          for(int i=0;i<n/2;i++){            cout<<2<<" ";          }cout<<endl;        }        else{          cout<<(n-3)/2+1<<endl;          cout<<3<<" ";          for(int i=0;i<(n-3)/2;i++){            cout<<2<<" ";          }cout<<endl;        }      }    }  }  return 0;}