#include<bits/stdc++.h>using namespace std;int main(){  int t;  cin>>t;  while(t--){    int n;    cin>>n;    vector<int>a(n);    for(int i=0;i<n;i++){      cin>>a[i];    }        vector<int>b=a;        int ans;    sort(b.begin(),b.end());        if(a!=b){      cout<<0<<endl;    }    else{      vector<int>gap(n-1);      for(int i=0;i<n-1;i++){        gap[i]=abs(b[i]-b[i+1]);      }      sort(gap.begin(),gap.end());      if(gap[0]%2!=0){        ans=((gap[0]-1)/2)+1;      }      else{        ans=(gap[0]/2)+1;      }      cout<<ans<<endl;    }     }  return 0;}